import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/Home.css';

export default function Home() {
  const colors = ['#3498db', '#e74c3c', '#2ecc71', '#9b59b6']; // Example background colors
  const [index, setIndex] = useState(0);
  const location = useLocation();

  useEffect(() => {
    if (location.pathname === '/') {
      const interval = setInterval(() => {
        setIndex((prevIndex) => (prevIndex + 1) % colors.length);
      }, 2000); // Change the background color every 2 seconds

      return () => clearInterval(interval); // Cleanup interval on component unmount
    }
  }, [location.pathname, colors.length]);

  const complementaryColor = (hex) => {
    const rgb = parseInt(hex.slice(1), 16);
    const invertedColor = (0xffffff ^ rgb).toString(16).padStart(6, '0');
    return `#${invertedColor}`;
  };

  const backgroundColor = colors[index];
  const textColor = complementaryColor(backgroundColor);

  return (
    <div className="home" style={{ backgroundColor: backgroundColor, color: textColor }}>
      <h1 key={index} className="plop-text">{['Developer', 'Designer', 'Creator', 'Innovator'][index]}</h1>
    </div>
  );
}
