import '../styles/Header.css';
import { Link, useMatch, useResolvedPath } from "react-router-dom";

export default function Header({ backgroundColor, textColor }) {
  const style = backgroundColor && textColor ? { backgroundColor, color: textColor } : {};

  return (
    <nav className="head" style={style}>
      <Link to="/" className="site-title" style={{ color: textColor || 'inherit' }}>
        Site Name
      </Link>
      <ul>
        <CustomLink to="/About" textColor={textColor}>About</CustomLink>
        <CustomLink to="/Experience" textColor={textColor}>Experience</CustomLink>
        <CustomLink to="/Work" textColor={textColor}>Work</CustomLink>
      </ul>
    </nav>
  );
}

function CustomLink({ to, children, textColor, ...props }) {
  const resolvedPath = useResolvedPath(to);
  const isActive = useMatch({ path: resolvedPath.pathname, end: true });

  return (
    <li className={isActive ? "active" : ""}>
      <Link to={to} {...props} style={{ color: textColor || 'inherit' }}>
        {children}
      </Link>
    </li>
  );
}
