import React, { useState, useEffect } from 'react';
import '../styles/Home.css'; // Import the CSS file for styling

export default function Home() {
  const words = ['Developer', 'Designer', 'Creator', 'Innovator'];
  const colors = ['#3498db', '#e74c3c', '#2ecc71', '#9b59b6']; // Example background colors
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % words.length);
    }, 2000); // Change the word every 2 seconds

    return () => clearInterval(interval); // Cleanup interval on component unmount
  }, [words.length]);

  const complementaryColor = (hex) => {
    const rgb = parseInt(hex.slice(1), 16); // Convert hex to RGB
    // Invert the RGB colors to get the complementary color
    const invertedColor = (0xffffff ^ rgb).toString(16).padStart(6, '0');
    return `#${invertedColor}`;
  };

  const backgroundColor = colors[index];
  const textColor = complementaryColor(backgroundColor);

  return (
    <div className="home" style={{ backgroundColor: backgroundColor, color: textColor }}>
      <h1 key={index} className="plop-text">{words[index]}</h1>
    </div>
  );
}
